const save_data_btn = document.querySelector(".save-data-btn");
const todle = document.querySelector('.todle');
const cancel = document.querySelector('.cancel');

if(cancel!=null){
    cancel.addEventListener('click',cancelfunction);
}
save_data_btn.addEventListener("click",saveData);

function cancelfunction(){
    todle.style.display = 'none';
}

function saveData(e){
    var click = e.target;
    var red0 = document.getElementById('red0').value;
    var red1 = document.getElementById('red1').value;
    var red2 = document.getElementById('red2').value;
    var red3 = document.getElementById('red3').value;
    var green0 = document.getElementById('green0').value;
    var green1 = document.getElementById('green1').value;
    var green2 = document.getElementById('green2').value;
    var green3 = document.getElementById('green3').value;
    var blue0 = document.getElementById('blue0').value;
    var blue1 = document.getElementById('blue1').value;
    var blue2 = document.getElementById('blue2').value;
    var blue3 = document.getElementById('blue3').value;
    var color0 = `#${rgbtohex(red0)}${rgbtohex(green0)}${rgbtohex(blue0)}`;
    var color1 =`#${rgbtohex(red1)}${rgbtohex(green1)}${rgbtohex(blue1)}`;
    var color2 = `#${rgbtohex(red2)}${rgbtohex(green2)}${rgbtohex(blue2)}`;
    var color3 = `#${rgbtohex(red3)}${rgbtohex(green3)}${rgbtohex(blue3)}`;
    var colordsss = colorOrder(color0,color1,color2,color3);
    var colordsssHex = colorOrderHex(color0,color1,color2,color3);
    var pc = colordsssHex[3];
    var sc1 = colordsssHex[2];
    var sc2 = colordsssHex[1];
    var sc3 = colordsssHex[0];
    var pcHue = colordsss[3];
    var sc1Hue = colordsss[2];
    var sc2Hue = colordsss[1];
    var sc3Hue = colordsss[0];
    let tag = [];
    var tags0 = hueval(pcHue);
    var tags1 = hueval(sc1Hue);
    var tags2 = hueval(sc2Hue);
    var tags3 = hueval(sc3Hue);
    for(var i = 0;i<tags0.length;i++){
        var tagval = tags0[i];
        tag.push(tagval);
    }for(var i = 0;i<tags1.length;i++){
        var tagval = tags1[i];
        tag.push(tagval);
    }
    for(var i = 0;i<tags2.length;i++){
        var tagval = tags2[i];
        tag.push(tagval);
    }
    for(var i = 0;i<tags3.length;i++){
        var tagval = tags3[i];
        tag.push(tagval);
    }
      if(click.classList == 'save-data-btn'){
        db.collection("colors").doc().set({
            pc:pc,
            sc1:sc1,
            sc2:sc2,
            sc3:sc3,
            tag:tag,
            createdAts: new Date().getTime(),
        }); 
        todle.style.display = 'flex';
    }

}



function hexToRgbToHsl(color){
    let colorRGB =  Color.hexToRgb(color);
    let colorHSL = Color.rgbToHsl(colorRGB);
    return colorHSL;
}

function hueval(hue){
    var tag;
    if(hue<359){
        tag = ["red"];
    }
    if(hue<357){
        tag = ['red','pink']
    }
    if(hue<340){
        tag = ['pink']
    }
    if(hue<286){
        tag = ['purple',"pink"];
    }
    if(hue<270){
        tag = ['purple',"blue"];
    }
    if(hue<250){
        tag=['blue'];
    }
    if(hue<200){
        tag = ['blue','cyan']
    }
    if(hue<160){
        tag = ['green']
    }
    if(hue<100){
        tag = ['green','yellow']
    }
    if(hue<70){
        tag = ['yellow','brown','green'];
    }
    if(hue<60){
        tag = ['yellow'];
    }
    if(hue<38){
        tag = ['orange','brown'];
    }
    if(hue<10){
        tag = ['red','pink'];
    }
    if(hue<10){
        tag = ['red','pink'];
    }
    return tag;  
   
}


function colorOrder(colorA0,colorA1,colorA2,colorA3){
    var colordss = [hexToRgbToHsl(colorA0)[2],hexToRgbToHsl(colorA1)[2],hexToRgbToHsl(colorA2)[2],hexToRgbToHsl(colorA3)[2]];
    colordss.sort();
    var c1 = hexToRgbToHsl(colorA0);
    var c2  = hexToRgbToHsl(colorA1);
    var c3  = hexToRgbToHsl(colorA2);
    var c4  = hexToRgbToHsl(colorA3);
    var c2Arr =[];
    for(var i = 0;i<colordss.length;i++){
        if(c1[2]==colordss[i]){
            c2Arr.push(c1[0]);
        }
        if(c2[2]==colordss[i]){
            c2Arr.push(c2[0]);
        }
        if(c3[2]==colordss[i]){
            c2Arr.push(c3[0]);
        }
        if(c4[2]==colordss[i]){
            c2Arr.push(c4[0]);
        }
    }
    // console.log(colordss);
    // console.log(c1);
    // console.log(c2);
    // console.log(c3);
    // console.log(c4);
    console.log(c2Arr[0]);
    return c2Arr;
}


function colorOrderHex(colorA0,colorA1,colorA2,colorA3){
    var colordss = [hexToRgbToHsl(colorA0)[2],hexToRgbToHsl(colorA1)[2],hexToRgbToHsl(colorA2)[2],hexToRgbToHsl(colorA3)[2]];
    colordss.sort();
    var c1 = hexToRgbToHsl(colorA0);
    var c2  = hexToRgbToHsl(colorA1);
    var c3  = hexToRgbToHsl(colorA2);
    var c4  = hexToRgbToHsl(colorA3);
    var c2Arr =[];
    for(var i = 0;i<colordss.length;i++){
        if(c1[2]==colordss[i]){
            c2Arr.push(colorA0);
        }
        if(c2[2]==colordss[i]){
            c2Arr.push(colorA1);
        }
        if(c3[2]==colordss[i]){
            c2Arr.push(colorA2);
        }
        if(c4[2]==colordss[i]){
            c2Arr.push(colorA3);
        }
    }
    // console.log(colordss);
    // console.log(c1);
    // console.log(c2);
    // console.log(c3);
    // console.log(c4);
    // console.log(c2Arr);
    return c2Arr;
}

